export type Pos = {
  start: number;
};

// These are used when `options.locations` is on, for the
// `startLoc` and `endLoc` properties.

export class Position {
  line: number;
  column: number;
  index: number;

  constructor(line: number, col: number, index: number) {
    this.line = line;
    this.column = col;
    this.index = index;
  }
}

export class SourceLocation {
  start: Position;
  end: Position;
  filename: string;
  identifierName: string | undefined | null;

  constructor(start: Position, end?: Position) {
    this.start = start;
    // (may start as null, but initialized later)
    this.end = end;
  }
}

/**
 * creates a new position with a non-zero column offset from the given position.
 * This function should be only be used when we create AST node out of the token
 * boundaries, such as TemplateElement ends before tt.templateNonTail. This
 * function does not skip whitespaces.
 *
 * @export
 * @param {Position} position
 * @param {number} columnOffset
 * @returns {Position}
 */
export function createPositionWithColumnOffset(
  position: Position,
  columnOffset: number,
) {
  const { line, column, index } = position;
  return new Position(line, column + columnOffset, index + columnOffset);
}
export const enum ParseErrorCode {
  SyntaxError = "BABEL_PARSER_SYNTAX_ERROR",
  SourceTypeModuleError = "BABEL_PARSER_SOURCETYPE_MODULE_REQUIRED",
}

export type SyntaxPlugin =
  | "flow"
  | "typescript"
  | "jsx"
  | "pipelineOperator"
  | "placeholders";

export type ToMessage<ErrorDetails> = (self: ErrorDetails) => string;

export type ParseErrorCredentials<ErrorDetails> = {
  code: ParseErrorCode;
  reasonCode: string;
  syntaxPlugin?: SyntaxPlugin;
  toMessage: ToMessage<ErrorDetails>;
};

const reflect = (keys: string[], last = keys.length - 1) => ({
  get(this: unknown): unknown {
    return keys.reduce(
      (object, key) =>
        // @ts-expect-error key should index object
        object[key],
      this,
    );
  },
  set(this: unknown, value: unknown) {
    keys.reduce(
      // @ts-expect-error key should index item
      (item, key, i) => (i === last ? (item[key] = value) : item[key]),
      this,
    );
  },
});

const instantiate = <T>(
  constructor: new () => T,
  properties: any,
  descriptors: any,
) =>
  Object.keys(descriptors)
    .map(key => [key, descriptors[key]])
    .filter(([, descriptor]) => !!descriptor)
    .map(([key, descriptor]) => [
      key,
      typeof descriptor === "function"
        ? { value: descriptor, enumerable: false }
        : typeof descriptor.reflect === "string"
        ? { ...descriptor, ...reflect(descriptor.reflect.split(".")) }
        : descriptor,
    ])
    .reduce(
      (instance, [key, descriptor]) =>
        Object.defineProperty(instance, key, {
          configurable: true,
          ...descriptor,
        }),
      Object.assign(new constructor(), properties),
    );

export { instantiate };
import { ParseErrorCode } from "../parse-error";

export default {
  ImportMetaOutsideModule: {
    message: `import.meta may appear only with 'sourceType: "module"'`,
    code: ParseErrorCode.SourceTypeModuleError,
  },
  ImportOutsideModule: {
    message: `'import' and 'export' may appear only with 'sourceType: "module"'`,
    code: ParseErrorCode.SourceTypeModuleError,
  },
};
const NodeDescriptions = {
  ArrayPattern: "array destructuring pattern",
  AssignmentExpression: "assignment expression",
  AssignmentPattern: "assignment expression",
  ArrowFunctionExpression: "arrow function expression",
  ConditionalExpression: "conditional expression",
  CatchClause: "catch clause",
  ForOfStatement: "for-of statement",
  ForInStatement: "for-in statement",
  ForStatement: "for-loop",
  FormalParameters: "function parameter list",
  Identifier: "identifier",
  ImportSpecifier: "import specifier",
  ImportDefaultSpecifier: "import default specifier",
  ImportNamespaceSpecifier: "import namespace specifier",
  ObjectPattern: "object destructuring pattern",
  ParenthesizedExpression: "parenthesized expression",
  RestElement: "rest element",
  UpdateExpression: {
    true: "prefix operation",
    false: "postfix operation",
  },
  VariableDeclarator: "variable declaration",
  YieldExpression: "yield expression",
};

type NodeTypesWithDescriptions = keyof Omit<
  typeof NodeDescriptions,
  "UpdateExpression"
>;

type NodeWithDescription =
  | {
      type: "UpdateExpression";
      prefix: boolean;
    }
  | {
      type: NodeTypesWithDescriptions;
    };

// @ts-expect-error prefix is specified only when type is UpdateExpression
// eslint-disable-next-line no-confusing-arrow
const toNodeDescription = ({ type, prefix }: NodeWithDescription) =>
  type === "UpdateExpression"
    ? NodeDescriptions.UpdateExpression[String(prefix) as "true" | "false"]
    : NodeDescriptions[type];

export default toNodeDescription;
import toNodeDescription from "./to-node-description";

export type LValAncestor =
  | { type: "UpdateExpression"; prefix: boolean }
  | {
      type:
        | "ArrayPattern"
        | "AssignmentExpression"
        | "CatchClause"
        | "ForOfStatement"
        | "FormalParameters"
        | "ForInStatement"
        | "ForStatement"
        | "ImportSpecifier"
        | "ImportNamespaceSpecifier"
        | "ImportDefaultSpecifier"
        | "ObjectPattern"
        | "RestElement"
        | "VariableDeclarator";
    };

